let mystep= case( ['_step'] == '5m', 5m, ['_step'] == '10m', 10m, ['_step'] == '30m', 30m, ['_step'] == '1h', 1h, 1d);
let endtime= case(["_forecast"]=="3h", 3h, ["_forecast"]=="12h", 12h, ["_forecast"]=="1d", 1d, ["_forecast"]=="2d", 2d, ["_forecast"]=="7d", 7d, 0s);
let end = ['_endTime'] + endtime;
let endTimeHour = case(["_forecast"]=="3h", 3, ["_forecast"]=="12h", 12, ["_forecast"]=="1d", 24, ["_forecast"]=="2d", 24*2, ["_forecast"]=="7d", 24*7, 0);
TempHumDevice
| where Timestamp  between (['_startTime'] ..  end)
| where IotHubDeviceId  in (_deviceId) or isempty(_deviceId )
| make-series humidity = avg(Humidity) default=0 on Timestamp from ['_startTime'] to end step mystep
| extend forecast = series_decompose_forecast(humidity,3)
| project Timestamp, humidity, forecast, end, endTimeHour

