let a = TempHumDevice | summarize any( Location) by IotHubDeviceId;
let b = MagneticContactDevice | summarize any( Location) by IotHubDeviceId;
let c = SwitchDevice | summarize any( Location) by IotHubDeviceId;
let d = MultySensorDevice | summarize any( Location) by IotHubDeviceId;
union a,b,c,d
| project IotHubDeviceId, location = any_Location

let a = TempHumDevice | summarize max(Timestamp) by IotHubDeviceId;
let b = MagneticContactDevice | summarize max(Timestamp) by IotHubDeviceId;
let c = SwitchDevice | summarize max(Timestamp) by IotHubDeviceId;
let d = MultySensorDevice | summarize max(Timestamp) by IotHubDeviceId;
union a,b,c,d
| project  IotHubDeviceId, Last_Device_payload = max_Timestamp
| extend Device_Status = iff(now() - Last_Device_payload > 12h, "Offiline", "Online")
